import React, { useContext, useState } from 'react';
import { SectionList, TextInput, TouchableOpacity, StyleSheet, View, Text, Image } from 'react-native';

import { zutaten } from '../utils/tempDataList';

export default function Salatbar({ route, navigation }) {
    const { cat } = route.params;


    var zutaten2 = []

    console.log('Number of elements: ' +zutaten.length)

    for(var i = 0; i < zutaten.length; i++){
        console.log("Recipe number: " +i)
        console.log(zutaten[i].name)
        console.log(zutaten[i].categories)

        var n = zutaten[i].categories.length
        console.log("Number of categories: " +n)

        for(var j = 0; j < n; j++){
            console.log("Category number: " +j)
            console.log("The category is: " +zutaten[i].categories[j])
            if(zutaten[i].categories[j] == cat){
                console.log("Gefunden")
                console.log(zutaten[i])
                zutaten2.push(zutaten[i])
            } else {
                console.log("Nicht gefunden")
            }

        }
    }

    return (
        <View style={styles.container}>
          
                      
            <SectionList
                sections={zutaten2}
                renderItem={({ item }) =>
                    <View>
                                   
                        {/* <Text style={{ fontSize: 14, fontFamily: 'cursive' }}>    
                            
                            {item.menge}
                            {item.einheit} {item.zutat}
                  

                        </Text> */}
                    </View>
                }

                renderSectionHeader={({ section }) =>
                <TouchableOpacity  onPress={() => {
                    console.log("Klicked on " +section.name)
                    console.log("Id:  " +section.receipeID)
                     navigation.navigate('Salatbar2', {receipeID: section.receipeID})
                
                }
                    
                    }>

                    <View style={styles.alignedInARow}>
                        <Image style={styles.smallImage} source={section.image} /> 
                        <View style={styles.smallText}>
                            <Text style={{ fontSize: 15, fontFamily: 'cursive' }}>
                                {section.name}
                            </Text>
                            <Text style={{ fontSize: 10, fontFamily: 'cursive' }}>
                                {section.beschreibung}
                            </Text>
                        </View>
                    </View>    

                    {/* <View style={styles.sectionHeaderStyle}>
                        <Image style={styles.image} source={section.image} />    
                        <Text style={{ fontSize: 22, fontFamily: 'cursive' }}> 
                        {section.name}
                        </Text>
                        <Text style={{ fontSize: 12, fontFamily: 'cursive' }}>
                        {section.beschreibung}
                        </Text>
                    </View> */}
                    </TouchableOpacity>     
                }
            />

        </View>
    );

}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        color: 'white',
        alignItems: 'center',
        justifyContent: 'center',
    },

    sectionHeaderStyle: {
        backgroundColor: 'white',
        // alignItems: 'left',
        justifyContent: 'center',
        paddingHorizontal: 5,
        paddingVertical: 10,
        width: 480,
    },

    image: {
        width: 480,
        height: 280,
        paddingHorizontal: 5,
        paddingVertical: 60,
        alignItems: 'center',
        justifyContent: 'center',
    },

    smallImage: {
        width: 48,
        height: 28,
        paddingHorizontal: 1,
        paddingVertical: 20,
        alignItems: 'center',
        justifyContent: 'center',
    },

    alignedInARow: {
        flex: 1,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: 1,
    },

    smallText: {
        backgroundColor: 'white',
        justifyContent: 'center',
        paddingHorizontal: 5,
        paddingVertical: 10,
        width: 300,
    },

});
