import React from 'react';
import { User } from '../classes/user';

export const insertUser = async () => {
    try {
        let response = await fetch(
            'http://134.102.137.196/:8080/eatforfitserver-0.0.1-SNAPSHOT/api/user/insert', {
            method: 'GET',
        });
        let json = await response.json();
        //console.log(json);
        return json;
    } catch (error) {
        console.error(error);
    }
};

export const getAllUsers = async () => {
    try {
        let response = await fetch(
            'http://192.168.178.24:8080/api/users/get', {
            method: 'GET',
        });
        let json = await response.json();
        //console.log(json);
        return json;
    } catch (error) {
        console.error(error);
    }
};

export const getUserByKey = async (userKey) => {
    try {
        let response = await fetch(
            'http://192.168.178.24:8080/api/user/get', {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(
                {
                    userKey: userKey,
                }
            )
        });
        let json = await response.json();
        //console.log(json);
        return json;
    } catch (error) {
        console.error(error);
    }
};

export const saveUser = async user => {
    try {
        let response = await fetch(
            'http://192.168.178.24:8080/api/user/save', {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(user)
        });
        let json = await response.json();
        //console.log(json);
        return json;
    } catch (error) {
        console.error(error);
    }
};

export const deleteUser = async user => {
    try {
        fetch(
            'http://192.168.178.24:8080/api/user/delete', {
            method: 'DELETE',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(user)
        });
    } catch (error) {
        console.error(error);
    }
};
