import React from 'react';   
//import Slick from 'react-native-slick';
import { TouchableOpacity, StyleSheet, View, Text } from 'react-native';
import {DataContext} from '../utils/DataManager';





export default function Home({route, navigation}) {

    
    const categories =   React.useContext(DataContext);
    const [visible, setVisible] = React.useState(false);

    const openMenu = () => setVisible(true);
    const closeMenu = () => setVisible(false);
    

    return (
            <View style={styles.container}>
            
                <TouchableOpacity onPress={() => {navigation.navigate('Rezept')}}>
                    <Text>Rezept</Text>
                </TouchableOpacity>  
                <TouchableOpacity onPress={() => {navigation.navigate('Salatbar', {cat: 'Salate'})}}>
                    <Text>Salatbar</Text>
                </TouchableOpacity>  
                <TouchableOpacity onPress={() => {navigation.navigate('Salatbar', {cat: 'Vegan'} )}}>
                <Text>Vegan</Text>
                </TouchableOpacity>   

            </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex:1,        
    },
    wrapper: {
        
    },
    slide1: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: 'red',
    },
    slide2: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: 'green',
    },
    slide3: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: 'blue',
    },
    text: {
      color: '#fff',
      fontSize: 30,
      fontWeight: 'bold',
    }
  
});
